import error from '../common/error';

export default {
  /**
   * @edata <directly>
   * @typedef {Object} ErrorInfo 报错信息
   * @property {string} message 报错信息
   * @property {Array.<string>} stack 报错堆栈
   * @property {number} line 报错行
   * @property {number} column 报错列
   * @property {Object} Error 报错原对象
   */
  /**
   * @edata <directly>
   * @callback ErrorCallback 报错回调
   * @param {ErrorInfo} err 报错信息
   */
  /**
   * 监听全局报错
   * @eapi <directly>
   * @param {ErrorCallback} callback 报错监听
   * @returns {void} 调用结果
   */
  onError(callback) {
    return error.onError(callback);
  },

  /**
   * 取消监听全局报错
   * @eapi <directly>
   * @param {ErrorCallback} [callback] 要移除的监听
   * @returns {void} 调用结果
   */
  offError(callback) {
    return error.offError(callback);
  }
}
