import EventManager from '../manager/eventManager';

export const EVENTS = {
  /**
   * SDK 初始化
   */
  SDK_INITED: 'SDK_INITED',
  /**
   * 未捕获异常
   */
  ERR_UNC: 'ERR_UNC',
  /**
   * 参数错误
   */
  ERR_PARAM: 'ERR_PARAM',
  /**
   * API Reject
   */
  ERR_REJECT: 'ERR_REJECT',
  /**
   * 接口调用
   */
  API_CALL: 'API_CALL',
  /**
   * API Resolve
   */
  API_RESOLVE: 'API_RESOLVE',
  /**
   * 警告信息
   */
  WARN_INFO: 'WARN_INFO'
}

/**
 * 全局事件
 */
class GEventer extends EventManager {
  constructor() {
    super();

    this.EVENTS = EVENTS
  }
}

/**
 * 系统事件处理器，比如 onError
 */
export default new GEventer();