export default {
  /**
   * 配置缓存
   * @private
   */
  _config: {},

  /**
   * 实例化
   * @param {Object} options 要存储的配置信息
   * @param {string} options.env 系统运行环境 'dev' | 'prod'
   * @param {string} options.version 当前sdk版本号
   */
  setConfig(options) {
    Object.assign(this._config, options || {});
  },

  /**
   * 获取当前配置信息
   * @returns {{ env: string, version: string }} config 系统配置信息
   */
  getConfig() {
    return this._config;
  },

  /**
   * 当前是否是开发环境
   */
  isDev() {
    return this._config.env === 'dev';
  },

  /**
   * 当前是否是正式环境
   */
  isProd() {
    return this._config.env === 'prod'
  }
}