import common from '../common';

const MODULE_NAME = 'MiniAppBackend';

export default {
  /**
   * 后台
   * @namespace backend
   */
  backend: {
    /**
     * 通用查询后台状态接口
     * @param {Object} params 
     * @param {string} params.key 关键字
     * @param {Object} params.param 参数
     */
    commonQuery(params) {
      return common.core.callEvent(MODULE_NAME, 'commonQuery', params);
    }
  }
}
