import common from '../common';

const MODULE_NAME = 'MiniAppFs';

export default {
  /**
   * 文件系统
   * @namespace fs
   */
  fs: {
    /**
     * 调取本地相册
     */
    uploadImg() {
      return common.core.callEvent(MODULE_NAME, 'uploadImg');
    },

    /**
     * 监听离线资源是否下载完成消息
     * @param {Object} params 
     * @param {string} params.url 资源地址
     * @returns {Promise<string>}
     */
    offlineFileReady(params) {
      return common.core.callEvent(MODULE_NAME, 'offlineFileReady', params);
    },
  }
}
