import common from '../common';

const MODULE_NAME = 'MiniAppOrder';

export default {
  /**
   * 秩序
   * @namespace order
   */
  order: {
    /**
     * 发起小程序文本的秩序审核
     * @param {Object} param 参数
     * @param {string} param.text 要检查的字符串
     */
    reportText(param) {
      return common.core.callEvent(MODULE_NAME, 'reportText', param);
    }
  }
}
