import common from '../common';

const MODULE_NAME = 'MiniAppPanel';

export default {
  /**
   * 面板
   * @namespace panel
   */
  panel: {
    /**
     * 改变小程序面板的布局，包括显示位置和显示大小
     * @param {boolean} visible 是否可见，默认true,
     * @param {string} res 取值 'PLAYER'
     * @param {number} x 横坐标
     * @param {number} y 纵坐标
     * @param {number} width 宽度
     * @param {number} height 高度
     */
    setLayout(params) {
      return common.core.callEvent(MODULE_NAME, 'setLayout', params);
    },

    /**
     * 销毁当前小程序
     */
    disposal() {
      return common.core.callEvent(MODULE_NAME, 'disposal');
    }
  }
}
