import callManager from './callManager';
import emitManager from './emitManager';


export default {
  /**
   * 注册监听事件
   */
  on(eventName, dispatchback, tp) {
    return emitManager.on(eventName, dispatchback, tp);
  },

  /**
   * 取消监听事件
   */
  off(eventName, dispatchback, tp) {
    return emitManager.off(eventName, dispatchback, tp);
  },

  /**
   * 触发注册的监听事件
   */
  emit(eventName, data) {
    return callManager.emit(eventName, data);
  },

  /**
   * 调用远端事件
   * @returns {Primise<any>}
   */
  dispatch(eventName, data) {
    return callManager.dispatch(eventName, data);
  },

  /**
   * Taf形式调用远端事件
   * @returns {Primise<any>}
   */
  dispatchTaf(eventName, data, ...params) {
    return callManager.dispatchTaf(eventName, data, ...params);
  }

}