import modeManager from '../common/modeManager';

export default {

  async request(params) {
    try {
      params.data = JSON.stringify(params.data)
    } catch (_) {
      params.data = null;
    }

    const MiniAppEBS = modeManager.getModule('MiniAppEBS') || {};
    return await MiniAppEBS.request(params);
  }

}