import messager from '../common/messager';

export default {
  getItem(key) {
    return messager.dispatch('getItem', { key });
  },

  setItem(key, value) {
    return messager.dispatch('setItem', { key, value });
  },

  removeItem(key) {
    return messager.dispatch('removeItem', { key });
  },

  getKeys() {
    return messager.dispatch('getKeys', {});
  },
}