import EventManager from '../manager/eventManager';

const eventer = new EventManager();

export default {
  /**
   * 错误类型
   */
  TYPES: {
    /**
     * 参数错误
     */
    PARAM: 'PARAM_ERR',
    /**
     * 接口错误
     */
    REJECT: 'REJECT_ERR'
  },

  /**
   * 触发全局报错
   * @param {string} event 类型
   * @param {Object} err 
   */
  emit(event, err) {
    return eventer.emit(event, err);
  },

  /**
   * 监听报错
   * @param {string} event 类型
   * @param {Function} fun 
   */
  on(event, fun) {
    return eventer.on(event, fun);
  },

  /**
   * 取消监听报错
   * @param {string} event 类型
   * @param {Function} fun 
   */
  off(event, fun) {
    return eventer.off(event, fun);
  },
}
